E1.chartScript:
/* Sales Chart
 * This chart visualizes monthly sales data
 */

/* Get data from spreadsheet cells */
months = [A2, A3, A4, A5, A6]
salesData = [B2, B3, B4, B5, B6]
expenseData = [C2, C3, C4, C5, C6]

/* Create Chart.js configuration */
chartConfig = {
  "type": "bar",
  "data": {
    "labels": months,
    "datasets": [
      {
        "label": "Sales",
        "data": salesData,
        "backgroundColor": "rgba(33, 150, 243, 0.6)",
        "borderColor": "rgba(33, 150, 243, 1)",
        "borderWidth": 2
      },
      {
        "label": "Expenses",
        "data": expenseData,
        "backgroundColor": "rgba(255, 99, 132, 0.6)",
        "borderColor": "rgba(255, 99, 132, 1)",
        "borderWidth": 2
      }
    ]
  },
  "options": {
    "responsive": true,
    "plugins": {
      "title": {
        "display": true,
        "text": "Monthly Sales vs Expenses",
        "font": {
          "size": 18
        }
      },
      "legend": {
        "display": true,
        "position": "top"
      }
    },
    "scales": {
      "y": {
        "beginAtZero": true,
        "ticks": {
          "callback": "function(value) { return '$' + value.toLocaleString(); }"
        }
      }
    }
  }
}

E3.chartScript:
/* Sales Distribution Pie Chart
 * Shows percentage of total sales per month
 */

/* Get data from cells */
months = [A2, A3, A4, A5, A6]
salesData = [B2, B3, B4, B5, B6]

/* Create pie chart configuration */
chartConfig = {
  "type": "pie",
  "data": {
    "labels": months,
    "datasets": [{
      "label": "Sales Distribution",
      "data": salesData,
      "backgroundColor": [
        "rgba(255, 99, 132, 0.8)",
        "rgba(54, 162, 235, 0.8)",
        "rgba(255, 206, 86, 0.8)",
        "rgba(75, 192, 192, 0.8)",
        "rgba(153, 102, 255, 0.8)"
      ],
      "borderColor": [
        "rgba(255, 99, 132, 1)",
        "rgba(54, 162, 235, 1)",
        "rgba(255, 206, 86, 1)",
        "rgba(75, 192, 192, 1)",
        "rgba(153, 102, 255, 1)"
      ],
      "borderWidth": 2
    }]
  },
  "options": {
    "responsive": true,
    "plugins": {
      "title": {
        "display": true,
        "text": "Sales Distribution by Month",
        "font": {
          "size": 18
        }
      },
      "legend": {
        "position": "right"
      }
    }
  }
}

E5.chartScript:
/* Sales and Expenses Trend
 * Line chart showing trends over time
 */

months = [A2, A3, A4, A5, A6]
salesData = [B2, B3, B4, B5, B6]
expenseData = [C2, C3, C4, C5, C6]

/* Calculate profit */
profitData = []
DO i = 1 TO 5
  profit = salesData[i] - expenseData[i]
  profitData = profitData || [profit]
END

chartConfig = {
  "type": "line",
  "data": {
    "labels": months,
    "datasets": [
      {
        "label": "Sales",
        "data": salesData,
        "borderColor": "rgba(33, 150, 243, 1)",
        "backgroundColor": "rgba(33, 150, 243, 0.1)",
        "borderWidth": 3,
        "fill": true,
        "tension": 0.4
      },
      {
        "label": "Expenses",
        "data": expenseData,
        "borderColor": "rgba(255, 99, 132, 1)",
        "backgroundColor": "rgba(255, 99, 132, 0.1)",
        "borderWidth": 3,
        "fill": true,
        "tension": 0.4
      },
      {
        "label": "Profit",
        "data": profitData,
        "borderColor": "rgba(76, 175, 80, 1)",
        "backgroundColor": "rgba(76, 175, 80, 0.1)",
        "borderWidth": 3,
        "fill": true,
        "tension": 0.4
      }
    ]
  },
  "options": {
    "responsive": true,
    "plugins": {
      "title": {
        "display": true,
        "text": "Financial Trends",
        "font": {
          "size": 18
        }
      },
      "legend": {
        "position": "top"
      }
    },
    "scales": {
      "y": {
        "beginAtZero": true
      }
    },
    "interaction": {
      "mode": "index",
      "intersect": false
    }
  }
}
